contracts/LotteryGame.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {VRFCoordinatorV2Interface} from "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {VRFConsumerBaseV2} from "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import {ILotteryManager} from "./LotteryManager.sol";

error OnlyManager(string reason);
error NoFunds();

contract LotteryGame is VRFConsumerBaseV2, Ownable, ReentrancyGuard {
    ILotteryManager public manager;

    modifier onlyManager() {
        if (address(manager) != msg.sender) {
            revert OnlyManager("Only callable by lottery manager!");
        }
        _;
    }

    // VRF fields
    VRFCoordinatorV2Interface private immutable COORDINATOR;
    uint256 private immutable SUBSCRIPTION_ID;
    bytes32 private immutable KEY_HASH;
    uint32 private immutable CALLBACK_GAS_LIMIT = 100000;
    uint16 private immutable REQUEST_CONFIRMATIONS = 3;
    // VRF does not guarantee unique words, therefore, we fetch one random word which is then used deterministically
    // for determining the winners.
    uint32 private immutable NUM_WORDS = 1;

    mapping(uint256 => uint256) public requestIdToLotteryId;
    mapping(uint256 => uint256) public requestIdToNumberOfWinners;
    mapping(uint256 => uint256) public requestIdToLotteryPrizePool;

    mapping(address => uint256) public pendingWithdrawals;
    event WinnersDrawn(
        uint256 indexed lotteryId,
        ILotteryManager.ParticipationStruct[] winners,
        uint256 prizePerWinner,
        uint256 seed
    );
    event Deposit(address indexed to, uint256 amount);

    struct LotteryResult {
        bool completed;
        uint256 timestamp;
        ILotteryManager.ParticipationStruct[] winners;
        uint256 prizePerWinner;
        uint256 seed;
    }

    mapping(uint256 => LotteryResult) public lotteryResults;

    constructor(
        address vrfCoordinator,
        bytes32 _keyHash,
        uint256 subscriptionId,
        address managerAddress
    ) VRFConsumerBaseV2(vrfCoordinator) Ownable(msg.sender) {
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        KEY_HASH = _keyHash;
        SUBSCRIPTION_ID = subscriptionId;
        manager = ILotteryManager(managerAddress);
    }

    // only callable by the LotteryManager contract
    function drawWinners(
        uint256 lotteryId,
        uint256 numberOfWinners
    ) external payable onlyManager {
        uint256 expected = manager.getJackpot(lotteryId);
        if (msg.value != expected) revert NoFunds();

        // request random number
        uint256 requestId = COORDINATOR.requestRandomWords(
            KEY_HASH,
            uint64(SUBSCRIPTION_ID),
            REQUEST_CONFIRMATIONS,
            CALLBACK_GAS_LIMIT,
            NUM_WORDS
        );

        // save lotteryId under that request
        requestIdToLotteryId[requestId] = lotteryId;
        requestIdToNumberOfWinners[requestId] = numberOfWinners;
        requestIdToLotteryPrizePool[requestId] = msg.value;
    }

    // callback called by vrf
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        uint256 lotteryId = requestIdToLotteryId[requestId];
        uint256 numberOfWinners = requestIdToNumberOfWinners[requestId];

        ILotteryManager.LotteryStruct memory lottery = manager.getLottery(
            lotteryId
        );
        ILotteryManager.ParticipationStruct[] memory participants = manager
            .getLotteryParticipants(lotteryId);

        // select winners
        ILotteryManager.ParticipationStruct[] memory winners = _selectWinners(
            participants,
            numberOfWinners,
            randomWords[0]
        );

        // calculate prizes
        uint256 totalPrize = requestIdToLotteryPrizePool[requestId];
        uint256 platformFee = (totalPrize * lottery.servicePercent) / 100;
        uint256 prizePerWinner = (totalPrize - platformFee) / numberOfWinners;

        // 4) **deposit** into each recipientâ€™s withdrawal balance
        for (uint256 i = 0; i < winners.length; i++) {
            pendingWithdrawals[winners[i].account] += prizePerWinner;
            emit Deposit(winners[i].account, prizePerWinner);
        }
        // deposit the owner fee
        pendingWithdrawals[lottery.owner] += platformFee;
        emit Deposit(lottery.owner, platformFee);

        emit WinnersDrawn(lotteryId, winners, prizePerWinner, randomWords[0]);

        LotteryResult storage result = lotteryResults[lotteryId];
        result.completed = true;
        result.timestamp = block.timestamp;
        result.prizePerWinner = prizePerWinner;
        result.seed = randomWords[0];

        for (uint256 i = 0; i < winners.length; i++) {
            result.winners.push(winners[i]);
        }

        // cleanup
        delete requestIdToLotteryId[requestId];
        delete requestIdToNumberOfWinners[requestId];
        delete requestIdToLotteryPrizePool[requestId];
    }

    function _selectWinners(
        ILotteryManager.ParticipationStruct[] memory participants,
        uint256 _numberOfWinners,
        uint256 seed
    ) private pure returns (ILotteryManager.ParticipationStruct[] memory) {
        ILotteryManager.ParticipationStruct[]
            memory selected = new ILotteryManager.ParticipationStruct[](
                _numberOfWinners
            );
        uint256[] memory indices = _fisherYates(participants.length, seed);

        for (uint256 i = 0; i < _numberOfWinners; i++) {
            selected[i] = participants[indices[i]];
        }

        return selected;
    }

    // shuffle
    function _fisherYates(
        uint256 n,
        uint256 seed
    ) private pure returns (uint256[] memory) {
        uint256[] memory indices = new uint256[](n);
        for (uint256 i = 0; i < n; i++) indices[i] = i;

        for (uint256 i = n - 1; i > 0; i--) {
            // select
            uint256 j = uint256(keccak256(abi.encodePacked(seed, i))) % (i + 1);
            // swap
            (indices[i], indices[j]) = (indices[j], indices[i]);
        }

        return indices;
    }

    // recipients (winners & owner) call this to pull their ETH
    function withdrawPayments() external nonReentrant {
        uint256 amount = pendingWithdrawals[msg.sender];
        if (amount == 0) revert NoFunds();
        pendingWithdrawals[msg.sender] = 0;
        (bool ok, ) = payable(msg.sender).call{value: amount}("");
        if (!ok) revert NoFunds();
    }

    function getLotteryResults(
        uint256 lotteryId
    ) external view returns (LotteryResult memory) {
        return lotteryResults[lotteryId];
    }

    receive() external payable {}
}

contracts/LotteryManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

error NoLotteryOwner(string reason);
error OnlyGame(string reason);
error SimpleError(string reason);

interface ILotteryManager {
    struct LotteryStruct {
        uint256 id;
        string title;
        string description;
        string imageURL;
        uint256 ticketPrice;
        uint256 numOfParticipants;
        uint256 servicePercent;
        bool drawn;
        address owner;
        uint256 createdAt;
        uint256 expiresAt;
    }
    struct ParticipationStruct {
        address account;
        string lotteryNumber;
    }
    struct LuckyNumber {
        string number;
        bool isUsed;
    }

    function createLottery(
        string memory title,
        string memory description,
        string memory imageURL,
        uint256 prize,
        uint256 ticketPrice,
        uint256 expiresAt
    ) external;

    function importLuckyNumbers(
        uint256 id,
        string[] memory luckyNumbers
    ) external;
    function buyTicket(uint256 id, uint256 luckyNumberIndex) external payable;
    function getAvailableLuckyNumbers(
        uint256 lotteryId
    ) external view returns (string[] memory available);
    function getLotteryParticipantsAddresses(
        uint256 id
    ) external view returns (address[] memory);
    function getLotteryParticipants(
        uint256 id
    ) external view returns (ParticipationStruct[] memory);
    function getJackpot(uint256 lotteryId) external view returns (uint256);
    function getLottery(
        uint256 lotteryId
    ) external view returns (LotteryStruct memory);
    function getLotteries() external view returns (LotteryStruct[] memory);
}

interface ILotteryGame {
    function drawWinners(
        uint256 lotteryId,
        uint256 numberOfWinners
    ) external payable;
}

contract LotteryManager is Ownable, ILotteryManager {
    ILotteryGame public game;
    uint256 private _totalLotteries;
    uint256 private constant MAX_WINNERS = 50;
    uint256 public platformBalance;

    // mappings
    mapping(uint256 => LotteryStruct) private lotteries;
    mapping(uint256 => ParticipationStruct[]) private lotteryParticipants;
    mapping(uint256 => LuckyNumber[]) private lotteryLuckyNumbers;

    // events (define log schema)
    event LotteryCreated(uint256 indexed id, address indexed owner);
    event TicketBought(uint256 indexed id, address indexed owner);
    event LuckyNumbersImported(uint256 indexed id, uint256 count);

    // using this makes sure that the function is only executable by the owner of the lottery.
    modifier onlyLotteryOwner(uint256 id) {
        if (lotteries[id].owner != msg.sender) {
            revert NoLotteryOwner("Only callable by owner!");
        }
        _;
    }

    modifier onlyGame() {
        if (address(game) != msg.sender) {
            revert OnlyGame("Only callable by lottery game!");
        }
        _;
    }
    constructor() Ownable(msg.sender) {}

    function setGame(address _game) public onlyOwner {
        game = ILotteryGame(_game);
    }

    // everyone can create a Lottery
    function createLottery(
        string memory title,
        string memory description,
        string memory imageURL,
        uint256 ticketPrice,
        uint256 servicePercent,
        uint256 expiresAt
    ) external {
        // sanity checks
        if (bytes(title).length == 0)
            revert SimpleError("Title cannot be empty");
        if (ticketPrice == 0)
            revert SimpleError("Ticket Prize must be positive");
        if (expiresAt <= block.timestamp)
            revert SimpleError("Expiration must be in future");

        uint256 lotteryId = _totalLotteries;
        _totalLotteries += 1;

        // create new lottery
        LotteryStruct memory newLottery = LotteryStruct({
            id: lotteryId,
            title: title,
            imageURL: imageURL,
            description: description,
            ticketPrice: ticketPrice,
            servicePercent: servicePercent,
            numOfParticipants: 0,
            drawn: false,
            owner: msg.sender,
            createdAt: block.timestamp,
            expiresAt: expiresAt
        });

        // add lottery
        lotteries[lotteryId] = newLottery;
        // Fire event
        emit LotteryCreated(lotteryId, msg.sender);
    }

    // owner uploads n lucky numbers
    function importLuckyNumbers(
        uint256 lotteryId,
        string[] memory luckyNumbers
    ) external onlyLotteryOwner(lotteryId) {
        if (lotteryLuckyNumbers[lotteryId].length != 0)
            revert SimpleError("Already imported");
        if (lotteries[lotteryId].numOfParticipants != 0)
            revert SimpleError("Participants already joined");
        if (luckyNumbers.length == 0) revert SimpleError("Empty list");

        for (uint256 i = 0; i < luckyNumbers.length; i++) {
            lotteryLuckyNumbers[lotteryId].push(
                LuckyNumber({number: luckyNumbers[i], isUsed: false})
            );
        }
        emit LuckyNumbersImported(lotteryId, luckyNumbers.length);
    }

    // function callable by anyone
    function buyTicket(
        uint256 lotteryId,
        uint256 luckyNumberIndex
    ) external payable {
        // reference lottery on chain
        LotteryStruct storage lottery = lotteries[lotteryId];
        if (msg.value < lottery.ticketPrice)
            revert SimpleError("Insufficient ETH");
        if (block.timestamp > lottery.expiresAt)
            revert SimpleError("Cannot buy Tickets anymore.");
        if (lottery.drawn) revert SimpleError("Lottery already drawn!");

        LuckyNumber storage luckyNumber = lotteryLuckyNumbers[lotteryId][
            luckyNumberIndex
        ];
        // use index, because it is more gas friendly than string comparison using hashing
        if (luckyNumber.isUsed) revert SimpleError("Number already used!");

        lotteryParticipants[lotteryId].push(
            ParticipationStruct({
                account: msg.sender,
                lotteryNumber: luckyNumber.number
            })
        );

        lottery.numOfParticipants++;
        luckyNumber.isUsed = true;

        emit TicketBought(lotteryId, msg.sender);
    }

    // draw winners
    function drawWinners(
        uint256 lotteryId,
        uint256 numberOfWinners
    ) external onlyLotteryOwner(lotteryId) {
        ILotteryManager.LotteryStruct storage lottery = lotteries[lotteryId];
        if (lottery.drawn) revert SimpleError("Lottery already drawn");
        //        if(block.timestamp < lottery.expiresAt) revert SimpleError ("Lottery not expired yet");
        if (numberOfWinners < 1)
            revert SimpleError("Must have at least one winner");
        if (
            numberOfWinners > lottery.numOfParticipants ||
            numberOfWinners > MAX_WINNERS
        ) revert SimpleError("Too many winners");

        // forward to game
        uint256 jackpot = lottery.ticketPrice * lottery.numOfParticipants;
        lottery.drawn = true;
        game.drawWinners{value: jackpot}(lotteryId, numberOfWinners);
    }

    function getAvailableLuckyNumbers(
        uint256 lotteryId
    ) external view returns (string[] memory available) {
        LuckyNumber[] storage all = lotteryLuckyNumbers[lotteryId];

        uint256 count = 0;
        for (uint256 i = 0; i < all.length; i++) {
            if (!all[i].isUsed) {
                count++;
            }
        }

        available = new string[](count);

        uint256 j = 0;
        for (uint256 i = 0; i < all.length; i++) {
            if (!all[i].isUsed) {
                available[j] = all[i].number;
                j++;
            }
        }

        return available;
    }

    function getLottery(
        uint256 lotteryId
    ) external view returns (LotteryStruct memory) {
        return lotteries[lotteryId];
    }

    function getLotteries()
        external
        view
        returns (LotteryStruct[] memory _lotteries)
    {
        _lotteries = new LotteryStruct[](_totalLotteries);
        for (uint256 i = 0; i < _totalLotteries; i++) {
            _lotteries[i] = _lotteries[i];
        }
    }

    // getter for the lottery numOfParticipant
    function getLotteryParticipantsAddresses(
        uint256 lotteryId
    ) external view returns (address[] memory) {
        address[] memory _participations = new address[](
            lotteryParticipants[lotteryId].length
        );
        for (uint256 i = 0; i < lotteryParticipants[lotteryId].length; ++i) {
            _participations[i] = lotteryParticipants[lotteryId][i].account;
        }
        return _participations;
    }

    function getLotteryParticipants(
        uint256 lotteryId
    ) external view returns (ParticipationStruct[] memory) {
        ParticipationStruct[]
            memory _participations = new ParticipationStruct[](
                lotteryParticipants[lotteryId].length
            );
        for (uint256 i = 0; i < lotteryParticipants[lotteryId].length; ++i) {
            _participations[i] = lotteryParticipants[lotteryId][i];
        }
        return _participations;
    }

    function getJackpot(uint256 lotteryId) external view returns (uint256) {
        LotteryStruct storage lottery = lotteries[lotteryId];
        return lottery.ticketPrice * lottery.numOfParticipants;
    }

    function getLuckyNumbers(
        uint256 lotteryId
    ) external view returns (string[] memory luckyNumbers) {
        luckyNumbers = new string[](lotteryLuckyNumbers[lotteryId].length);
        for (uint256 i = 0; i < lotteryLuckyNumbers[lotteryId].length; ++i) {
            luckyNumbers[i] = lotteryLuckyNumbers[lotteryId][i].number;
        }
        return luckyNumbers;
    }

    function getTotalLotteries() external view returns (uint256) {
        return _totalLotteries;
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }
}
